import React, { useState, useEffect } from "react";
import axios from "axios";

function Home() {
  const username = localStorage.getItem("username");
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({
    name: "",
    email: "",
    os: "",
    dbms: "",
    ds: "",
    coa: "",
    java: ""
  });
  const [editingId, setEditingId] = useState(null);

  // Fetch all students
  const getStudents = async () => {
    try {
      const res = await axios.get("http://localhost:5000/students");
      setStudents(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    getStudents();
  }, []);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      if (editingId) {
        await axios.put(`http://localhost:5000/students/${editingId}`, form);
        setEditingId(null);
      } else {
        await axios.post("http://localhost:5000/students", form);
      }
      setForm({ name: "", email: "", os: "", dbms: "", ds: "", coa: "", java: "" });
      getStudents();
    } catch (err) {
      console.error(err);
      alert("Error saving data");
    }
  };

  const handleEdit = (student) => {
    setForm({
      name: student.name,
      email: student.email,
      os: student.os,
      dbms: student.dbms,
      ds: student.ds,
      coa: student.coa,
      java: student.java
    });
    setEditingId(student._id);
  };

  const handleDelete = async (id) => {
    try {
      await axios.delete(`http://localhost:5000/students/${id}`);
      getStudents();
    } catch (err) {
      console.error(err);
      alert("Error deleting student");
    }
  };

  const gradeOptions = ["O+", "A+", "A", "B+", "B", "C+", "C", "D+"];

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif" }}>
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px" }}>
        <h2>Student Marks Management</h2>
        <div>Profile: {username}</div>
      </div>

      <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
        <input
          name="name"
          placeholder="Name"
          value={form.name}
          onChange={handleChange}
          required
        />
        <input
          name="email"
          placeholder="Email"
          value={form.email}
          onChange={handleChange}
          required
        />

        {["os", "dbms", "ds", "coa", "java"].map((subject) => (
          <select
            key={subject}
            name={subject}
            value={form[subject]}
            onChange={handleChange}
            required
          >
            <option value="">Select {subject.toUpperCase()}</option>
            {gradeOptions.map((grade) => (
              <option key={grade} value={grade}>{grade}</option>
            ))}
          </select>
        ))}

        <button type="submit">{editingId ? "Update Student" : "Add Student"}</button>
      </form>

      <table border="1" cellPadding="10" cellSpacing="0">
        <thead>
          <tr>
            <th>Name</th>
            <th>Email</th>
            <th>OS</th>
            <th>DBMS</th>
            <th>DS</th>
            <th>COA</th>
            <th>Java</th>
            <th>CGPA</th>
            <th>Grade</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student._id}>
              <td>{student.name}</td>
              <td>{student.email}</td>
              <td>{student.os}</td>
              <td>{student.dbms}</td>
              <td>{student.ds}</td>
              <td>{student.coa}</td>
              <td>{student.java}</td>
              <td>{student.cgpa}</td>
              <td>{student.grade}</td>
              <td>
                <button onClick={() => handleEdit(student)}>Edit</button>
                <button onClick={() => handleDelete(student._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Home;
