import React, { useState, useEffect } from "react";
import axios from "axios";

function Home() {
  const username = localStorage.getItem("username");
  const [students, setStudents] = useState([]);
  const [form, setForm] = useState({ os: "", dbms: "", ds: "", coa: "", java: "" });
  const [editingId, setEditingId] = useState(null);

  // Fetch all student marks
  const getStudents = async () => {
    const res = await axios.get("http://localhost:5000/students");
    setStudents(res.data);
  };

  useEffect(() => {
    getStudents();
  }, []);

  const handleChange = e => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async e => {
    e.preventDefault();
    try {
      if (editingId) {
        await axios.put(`http://localhost:5000/students/${editingId}`, form);
        setEditingId(null);
      } else {
        await axios.post("http://localhost:5000/students", form);
      }
      setForm({ os: "", dbms: "", ds: "", coa: "", java: "" });
      getStudents();
    } catch (err) {
      console.error(err);
      alert("Error saving data");
    }
  };

  const handleEdit = student => {
    setForm({
      os: student.os,
      dbms: student.dbms,
      ds: student.ds,
      coa: student.coa,
      java: student.java
    });
    setEditingId(student._id);
  };

  const handleDelete = async id => {
    await axios.delete(`http://localhost:5000/students/${id}`);
    getStudents();
  };

  return (
    <div style={{ padding: "20px", fontFamily: "sans-serif" }}>
      <div style={{ display: "flex", justifyContent: "space-between", marginBottom: "20px" }}>
        <h2>Student Semester Marks</h2>
        <div>Profile: {username}</div>
      </div>

      <form onSubmit={handleSubmit} style={{ marginBottom: "20px" }}>
        <input name="os" placeholder="OS" value={form.os} onChange={handleChange} required />
        <input name="dbms" placeholder="DBMS" value={form.dbms} onChange={handleChange} required />
        <input name="ds" placeholder="DS" value={form.ds} onChange={handleChange} required />
        <input name="coa" placeholder="COA" value={form.coa} onChange={handleChange} required />
        <input name="java" placeholder="Java" value={form.java} onChange={handleChange} required />
        <button type="submit">{editingId ? "Update Marks" : "Add Marks"}</button>
      </form>

      <table border="1" cellPadding="10" cellSpacing="0">
        <thead>
          <tr>
            <th>OS</th>
            <th>DBMS</th>
            <th>DS</th>
            <th>COA</th>
            <th>Java</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map(student => (
            <tr key={student._id}>
              <td>{student.os}</td>
              <td>{student.dbms}</td>
              <td>{student.ds}</td>
              <td>{student.coa}</td>
              <td>{student.java}</td>
              <td>
                <button onClick={() => handleEdit(student)}>Edit</button>
                <button onClick={() => handleDelete(student._id)}>Delete</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Home;
